void *server_thread(void *port)
{

  int *server_port = (int *) port;

  int server_fd, client_fd, err;
  struct sockaddr_in server, client;
  char buf[BUFFER_SIZE];

  server_fd = socket(AF_INET, SOCK_STREAM, 0);
  if (server_fd < 0) on_error("Could not create socket\n");

  server.sin_family = AF_INET;
  server.sin_port = htons(*server_port);
  server.sin_addr.s_addr = htonl(INADDR_ANY);

  int opt_val = 1;
  setsockopt(server_fd, SOL_SOCKET, SO_REUSEADDR, &opt_val, sizeof opt_val);

  err = bind(server_fd, (struct sockaddr *) &server, sizeof(server));
  if (err < 0) on_error("Could not bind socket\n");

  err = listen(server_fd, 128);
  if (err < 0) on_error("Could not listen on socket\n");

  printf("Server is listening on %d\n", *server_port);
  int r;
  while (1) {
    socklen_t client_len = sizeof(client);
    client_fd = accept(server_fd, (struct sockaddr *) &client, &client_len);
    printf("New client connected %d:%d\n", client.sin_addr.s_addr, client.sin_port);

    if (client_fd < 0) on_error("Could not establish new connection\n");
    int l = 0;
    while (1) {
      // r = rand() % (max_number + 1 - min_number) + min_number;
      r = size_arr[l%arr_len];
      response = (char *) malloc(r*sizeof(char));
      response = strncpy(response, "This is a super response test", r);
      for (int i=strlen(response); i<r-2; i++)
          response[i] = 48;
      response[r-1] = 0;
      int n = recv(client_fd, buf, BUFFER_SIZE, 0);

      if (n <= 0)
      {
          printf("done reading\n;");
          break;
      }

      err = send(client_fd, response, strlen(response), 0);
      if (err < 0) on_error("Client write failed\n");

#ifdef DEBUG
           printf("<- [%d] %s\n-> [%d] %s\n", n, buf, err, response);
#endif
      l++;
      free(response);
    }
  }


}
int main (int argc, char *argv[]) {
  if (argc < 2) on_error("Usage: %s [port]\n", argv[0]);

  int ret, port[argc-1];
  char *endptr;

  for(int i=0; i < argc-1; i++)
  {
      port[i] = strtol(argv[i+1], &endptr, 10);
  }

  pthread_attr_t attr;
  pthread_t thread[argc-1];

  ret = pthread_attr_init(&attr);
  if (ret) on_error("ini thread attr failed\n");

  for(int i=0; i < argc-1; i++)
  {
      ret = pthread_create(&thread[i], &attr, server_thread, &port[i]);
      if (ret) on_error("pthread create failed\n");
  }

  for(int i=0; i < argc-1; i++)
  {
      ret = pthread_join(thread[i], NULL);
      if (ret) on_error("pthread join error\n");
  }

  return 0;
}
